import React from 'react';
import logo from "../images/logo.svg";
import {Link, Route, Switch} from "react-router-dom";

const Header = ({onLogout, email}) => {

    return (
        <header className="header">
            <img className="header__logo" src={logo} alt="Логотип Место"/>
            <div>
                {email && <p className="header__email">{email}</p>}
                <Switch>
                    <Route exact path="/">
                        <Link to="/sing-in" className="header__logout" onClick={onLogout}>Выйти</Link>
                    </Route>
                    <Route path="/sing-up">
                        <Link to="/sing-in" className="header__auth">Войти</Link>
                    </Route>
                    <Route path="/sing-in">
                        <Link to="/sing-up" className="header__auth">Регистрация</Link>
                    </Route>
                </Switch>
            </div>
        </header>
    )
}

export default Header
