import {Route, Redirect} from "react-router-dom";

const ProtectedRoute = ({ Component, isLoggedIn, ...props }) => {
    return (
        <Route>{
            () => props.isLoggedIn ? <Component {...props}/> : <Redirect to="/sign-in"/>
        }
        </Route>
    )
};

export default ProtectedRoute
