import {useState} from "react";
import {Link} from "react-router-dom";
import InfoToolTip from "./InfoToolTip";

const Register = ({onRegister, isCorrectly, onClose, isOpen}) =>{
    const [state, setState] = useState({
        email: '',
        password: '',
    })

    const handleChange = e => {
        const {name, value} = e.target;

        setState(old => ({
            ...old,
            [name]: value,
        }))
    }

    const handleSubmit = e => {
        e.preventDefault();

        const { email, password } = state;

        if (!email || !password) return;

        onRegister(email, password)
            .catch(err => {
                console.log(err);

                setState(old => ({
                    ...old
                }))
            });
    }

    return (
        <>
            <div className="auth">
                <h1 className="auth__title">Регистрация</h1>
                <form className="auth__form">
                    <input className="auth__input" type="email" placeholder="Email" value={state.email || ''} />
                    <input className="auth__input" type="password" placeholder="Пароль" value={state.password || ''} />
                    <button className="auth__submit" type="submit" onClick={handleSubmit}>Зарегистрироваться</button>
                </form>
                <p className="auth__text">Уже зарегистрированы? <Link to="/sing-in" className="auth__text">Войти</Link></p>
            </div>
            <InfoToolTip isCorrectly={isCorrectly} isOpen={isOpen} onClose={onClose}/>
        </>
    )
}

export default Register
