import React from 'react';



function Main(){
    const handleEditAvatarClick = () => {
        document.querySelector('#popup-avatar').classList.add('popup_open');
    }

    const handleEditProfileClick = () => {
        document.querySelector('#popup-edit').classList.add('popup_open');
    }

    const handleAddPlaceClick = () => {
        document.querySelector('#popup-add').classList.add('popup_open');
    }

    return (
        <main className="content">
            <section className="profile">
                <div className="profile__content">
                    <div className="profile__avatar-container">
                        <img src="src/components/App#" alt="Фотография исследователя" className="profile__avatar" onClick={handleEditAvatarClick}/>
                    </div>
                    <div className="profile__info">
                        <h1 className="profile__name"></h1>
                        <button type="button" className="profile__edit-button" aria-label="Редактировать" onClick={handleEditProfileClick}></button>
                        <p className="profile__job"></p>
                    </div>
                </div>
                <button type="button" className="profile__add-button" aria-label="Добавить фотографию" onClick={handleAddPlaceClick}></button>
            </section>
            <section className="snapshots">
                <ul className="snapshots__elements">

                </ul>
            </section>
        </main>
    )
}

export default Main
