import {useState, useEffect} from "react";
import Header from "./Header.js";
import Main from "./Main.js";
import Footer from "./Footer.js";
import PopupWithForm from "./PopupWithForm.js";
import EditProfilePopup from "./EditProfilePopup";
import ImagePopup from "./ImagePopup.js";
import {api} from "../utils/Api";
import CurrentUserContext from "../contexts/CurrentUserContext.js";
import EditAvatarPopup from "./EditAvatarPopup";
import AddPlacePopup from "./AddPlacePopup";
import Login from "./Login";
import Register from "./Register";
import {register, authorize, validateToken} from "../utils/auth";
import {Route, Switch, useHistory} from "react-router-dom";
import ProtectedRoute from "./ProtectedRoute";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [isInfoToolTipOpen, setIsInfoTooltipOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [isImageOpen, setIsImageOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState({});
  const [cards, setCards] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isCorrectly, setIsCorrectly] = useState(false);
  const [userEmail, setUserEmail] = useState('');
  const history = useHistory()

  const handleRegister = async (password, email) => {
      try {
          await register(password, email);
          setIsCorrectly(true);
          setIsInfoTooltipOpen(true);
      } catch (err) {
          console.log(err);
          setIsCorrectly(false);
          setIsInfoTooltipOpen(true);
      }
  }

  useEffect(() => {
      if(!isLoggedIn) return;

      history.push('/')
  }, [isLoggedIn])

  const handleLogin = async (password, email) => {
      try {
          const { token } = await authorize(password, email);
          const { data } = await validateToken(token);
          setUserEmail(data.email);
          setIsLoggedIn(true);
          localStorage.setItem('jwt', token);
      } catch (err) {
          console.log(err);
          setIsCorrectly(false);
          setIsInfoTooltipOpen(true);
      }
  }

  const checkToken = async () => {
      const token = localStorage.getItem('jwt');
      if (token) {
          try {
              const { data } = await validateToken(token);
              setUserEmail(data.email);
              setIsLoggedIn(true);
          } catch (err) {
              console.log(err);
              setIsLoggedIn(false);
          }
      }
  }

  useEffect(() => {
      checkToken()
  }, [])

  const handleLogout = () => {
      localStorage.removeItem('jwt');
      history.push('/sing-in');
  }

  const getCardsInfo = async () =>{
        try{
            const res = await api.getCards();
            setCards(res);
        }
        catch (err) {
            console.log(err)
        }
  }

  useEffect(()=>{
        getCardsInfo()
  }, [])

  const handleCardLike = async (card) => {
      try {
          const isLiked = card.likes.some(i => i._id === currentUser._id);
          const newCard = await api.changeLikeCardStatus(card._id, isLiked)
          setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
      }
      catch (err) {
          console.log(err)
      }
  }

  const handleCardDelete = async (card) => {
      try {
          await api.deleteCard(card._id)
          setCards((state) => state.filter((c) => c._id !== card._id));
      }
      catch (err) {
          console.log(err)
      }
  }

  const getFullUserInfo = async () =>{
        try{
            const info = await api.getUserInfo();
            setCurrentUser(info);
        }
        catch (err) {
            console.log(err)
        }
  }

  useEffect(()=>{
      getFullUserInfo();
  }, [])

  const closeAllPopups = () => {
      setIsEditProfilePopupOpen(false);
      setIsAddPlacePopupOpen(false);
      setIsEditAvatarPopupOpen(false);
      setIsImageOpen(false);
      setIsInfoTooltipOpen(false);
  }

  const handleEditAvatarClick = () => {
      setIsEditAvatarPopupOpen(true);
  }

  const handleEditProfileClick = () => {
      setIsEditProfilePopupOpen(true);
  }

  const handleAddPlaceClick = () => {
      setIsAddPlacePopupOpen(true);
  }

  const handleCardClick = (card) => {
      setSelectedCard(card);
      setIsImageOpen(true);
  }

  const handleAddPlaceSubmit = async (data) => {
        try {
            const newCard = await api.postCard(data);
            setCards([newCard, ...cards]);
            closeAllPopups();
        }
        catch (err) {
            console.log(err)
        }
  }

  const handleUpdateUser = async (data) =>{
        try {
            const info = await api.changeUserInfo(data);
            setCurrentUser(info);
            closeAllPopups();
        }
        catch (err) {
            console.log(err)
        }
  }

  const handleUpdateAvatar = async (data) =>{
        try {
            const avatar = await api.changeUserAvatar(data);
            setCurrentUser(avatar);
            closeAllPopups();
        }
        catch (err) {
            console.log(err)
        }
  }

  return (
      <CurrentUserContext.Provider value={currentUser}>
          <div className="page">
            <Header onLogout={handleLogout} email={userEmail}/>
            <Switch>
                <ProtectedRoute exact path="/" isLoggedIn={isLoggedIn}
                                onAddPlace={handleAddPlaceClick}
                                onEditProfile={handleEditProfileClick}
                                onEditAvatar={handleEditAvatarClick}
                                onCardClick={handleCardClick}
                                cards={cards}
                                handleCardLike={handleCardLike}
                                handleCardDelete={handleCardDelete}
                Component={Main}/>

                <Route path="/sing-in">
                    <Login
                        onLogin={handleLogin}
                        isOpen={isInfoToolTipOpen}
                        onClose={closeAllPopups}
                        isCorrectly={isCorrectly}/>
                </Route>
                <Route path="/sing-up">
                    <Register
                        onRegister={handleRegister}
                        isOpen={isInfoToolTipOpen}
                        onClose={closeAllPopups}
                        isCorrectly={isCorrectly}/>
                </Route>
            </Switch>
            <Footer/>
            <AddPlacePopup
              isOpen={isAddPlacePopupOpen}
              isClose={closeAllPopups}
              onAddPlace={handleAddPlaceSubmit}>
            </AddPlacePopup>
            <EditProfilePopup
                isOpen={isEditProfilePopupOpen}
                isClose={closeAllPopups}
                onUpdateUser={handleUpdateUser}>
            </EditProfilePopup>
            <EditAvatarPopup
                isOpen={isEditAvatarPopupOpen}
                isClose={closeAllPopups}
                onUpdateAvatar={handleUpdateAvatar}>
            </EditAvatarPopup>
            <PopupWithForm
                name='agree'
                ariaLabel='подтверждением'
                title='Вы уверены?'
                button='Да'/>
            <ImagePopup
                card={selectedCard}
                onClose={closeAllPopups}
                isOpen={isImageOpen}/>
          </div>
      </CurrentUserContext.Provider>
  );
}

export default App;
