import '../index.css'
import {useState, useEffect} from "react";
import Header from "./Header.js";
import Main from "./Main.js";
import Footer from "./Footer.js";
import PopupWithForm from "./PopupWithForm.js";
import EditProfilePopup from "./EditProfilePopup";
import ImagePopup from "./ImagePopup.js";
import {api} from "../utils/Api";
import CurrentUserContext from "../contexts/CurrentUserContext.js";
import EditAvatarPopup from "./EditAvatarPopup";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({});
  const [isImageOpen, setIsImageOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState({});

    async function getFullUserInfo(){
        try{
            const info = await api.getUserInfo();
            setCurrentUser(info);
        }
        catch (err) {
            console.log(err)
        }
    }

    useEffect(()=>{
        getFullUserInfo();
    }, [])

  const closeAllPopups = () => {
      setIsEditProfilePopupOpen(false);
      setIsAddPlacePopupOpen(false);
      setIsEditAvatarPopupOpen(false);
      setIsImageOpen(false);
  }

  const handleEditAvatarClick = () => {
      setIsEditAvatarPopupOpen(true);
  }

  const handleEditProfileClick = () => {
      setIsEditProfilePopupOpen(true);
  }

  const handleAddPlaceClick = () => {
      setIsAddPlacePopupOpen(true);
  }

  const handleCardClick = (card) => {
      setSelectedCard(card);
      setIsImageOpen(true);
  }

  const handleUpdateUser = async (data) =>{
        try {
            const info = await api.changeUserInfo(data);
            setCurrentUser(info);
            closeAllPopups();
        }
        catch (err) {
            console.log(err)
        }
  }

    const handleUpdateAvatar = async (data) =>{
        try {
            const avatar = await api.changeUserAvatar(data);
            setCurrentUser(avatar);
            closeAllPopups();
        }
        catch (err) {
            console.log(err)
        }
    }

  return (
      <CurrentUserContext.Provider value={currentUser}>
          <div className="page">
            <Header/>
            <Main
                onAddPlace={handleAddPlaceClick}
                onEditProfile={handleEditProfileClick}
                onEditAvatar={handleEditAvatarClick}
                onCardClick={handleCardClick}/>
            <Footer/>
            <PopupWithForm
              name={'add'}
              ariaLabel={'добавлением'}
              title={'Новое место'}
              button={'Создать'}
              isOpen={isAddPlacePopupOpen}
              isClose={closeAllPopups}>
                <label className="popup__label">
                  <input id="form-img-name" type="text" name="name" className="popup__input popup__input_type_name"
                         placeholder="Название" required minLength="2" maxLength="30"/>
                    <span className="popup__input-error form-img-name-error"/>
                </label>
                <label className="popup__label">
                  <input id="form-img-link" type="url" name="link" className="popup__input popup__input_type_job"
                         placeholder="Ссылка на изображение" required/>
                    <span className="popup__input-error form-img-link-error"/>
                </label>
            </PopupWithForm>
            <EditProfilePopup
                isOpen={isEditProfilePopupOpen}
                isClose={closeAllPopups}
                onUpdateUser={handleUpdateUser}>
            </EditProfilePopup>
            <EditAvatarPopup
                isOpen={isEditAvatarPopupOpen}
                isClose={closeAllPopups}
                onUpdateAvatar={handleUpdateAvatar}>
            </EditAvatarPopup>
            <PopupWithForm
                name={'agree'}
                ariaLabel={'подтверждением'}
                title={'Вы уверены?'}
                button={'Да'}/>
            <ImagePopup
                card={selectedCard}
                onClose={closeAllPopups}
                isOpen={isImageOpen}/>
          </div>
      </CurrentUserContext.Provider>
  );
}

export default App;
